import axios from 'axios';
import {CITY_ACQUIRED} from '../actions/types';

export const getCities = () => dispatch => {
    axios.get("/api/city").then(res => 
        dispatch({
        type: CITY_ACQUIRED,
        payload: res.data
        })
    );
};
