import axios from 'axios';
import {COMMENT_ACQUIRED} from './types';

export const getComments = () => dispatch => {
    console.log();
    axios.get(`/api/comments`).then(res => {
        console.log(res.data)
        dispatch({
        type: COMMENT_ACQUIRED,
        payload: res.data
        })
    }
    );
};
