import React, { Component } from 'react';
import {getCities} from '../actions/citiesActions';
import {connect} from 'react-redux'; 


// import barcelona from "../images/barcelona.jpg";
// import sydney from "../images/sydney.jpg";
// import vancouver from "../images/vancouver.jpg";




class Cities extends Component {
    constructor() {
      super();
      this.state = {
        search: ''
      };
    }

    updateSearch(event) {
      this.setState({search: event.target.value});
    }

  componentDidMount() {
    // axios.get("/api/city").then(res => {
    // console.log(res);
      // this.setState({ cities: res.data });
      this.props.getCities();
    }


  render() {
    console.log(this.props)
      let filteredCities = this.props.cities.cities.filter(
        (city) => {
          return city.name.toLowerCase().indexOf(this.state.search) !==-1;
        }
      );
      return (
      <div>
        <div>
            <h2>Cities</h2>
        </div>
        <div>
            <input
            type="text"
            value={this.state.search}
            ref="search"
            onChange={this.updateSearch.bind(this)}
            placeholder="type city here"
            />
            {filteredCities.map(city => {
       return (
          <ul key={city._id}>
            <li>{city.name} {city.country}</li>
          </ul>
        )
        })}
        </div>
      </div>
      );

    }
  }


  const mapStateToProps = (state) => ({
    cities: state.reducerTwo
  });



  // <div className="citiesInfo">
        // <h2>Barcelona</h2>
        //     <img src={barcelona} className="cityImg" alt="img"/>
        //     <h2>Sydney</h2>
        //     <img src={sydney} className="cityImg" alt="img"/>
        //     <h2>Vancouver</h2>
        //     <img src={vancouver} className="cityImg" alt="img"/>
        // </div>

  export default connect (mapStateToProps,{getCities})(Cities);
