import React, { Component } from 'react';
import {getActivities} from '../actions/activityActions';
import {connect} from 'react-redux';
import Slider from 'react-slick';
import styled from "styled-components";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const City = styled.img`
 height: 100px;
 width: 180px;
 padding: 0px 20px;
`;

class Activities extends Component {
    constructor() {
      super();
      this.state = {
        Activities: []
      };

      const settings = {
        dots: true,
        centerMode: true,
        infinite: true,
        centerPadding: "20px",
        slidesToShow: 4,
        slidesToScroll: 4
      };
    }


componentDidMount() {
    console.log(this.props)
    const itineraryLink = this.props.match.params.id
          this.props.getActivities(itineraryLink);

        } 

render() {
    return (
    <div><h4>Activities</h4>
    {this.props.Activities.activities.map(activity => {
        return(

        <div key={activity._id}>
        <Slider {...settings}>
            <img className="activityPic" src={activity.activityPic} alt="activityPic"/>
            <div>{activity.activity}</div>
        </Slider>
        </div>
            )
        }
    )
}
</div>
    )
}
}


const mapStateToProps = (state) => ({
    Activities: state.reducerThree
  });

export default connect(mapStateToProps, {getActivities})(Activities);
