import axios from 'axios';
import {ITINERARY_ACQUIRED} from './types';

export const getItineraries = () => dispatch => {
    console.log("response");
    const city = this.props.match.params.city._id 
    axios.get(`/api/itinerary/${city}`).then(res => {
        console.log(res.data)
        dispatch({
        type: ITINERARY_ACQUIRED,
        payload: res.data
        })
    }
    );
};
